mapper.update:
UPDATE contents
SET
    title = #{title},
    url = #{url},
    description = #{description},
    thumbnail_url = #{thumbnailUrl}
WHERE
    contents_no = #{contentsNo}
;

mapper.select:
SELECT c.contents_no, tc.contents_type_no, tc.site_no, tc.target_contents_no, tc.target_contents_name, c.title, c.url, c.description, c.thumbnail_url, c.reg_date FROM contents c JOIN target_contents tc ON c.target_contents_no = tc.target_contents_no WHERE 1=1
AND title LIKE '%' || #{title} || '%'
AND tc.contents_type_no IN
#{contentsTypeNo}
limit #{limit} offset #{offset}
